mapper.select:
select T.* from ACT_TASK T inner join ACT_TASKINVOLVEMENT I ON T.ID_ = I.TASK_ID_
where T.ASSIGNEE_ IS NULL
      AND I.TYPE_ = 'candidate'


    AND (I.USER_ID_ = #{userId, jdbcType=VARCHAR}


    (




     OR I.GROUP_ID_ = #{groupId, jdbcType=VARCHAR}  


)

select distinct(T.*)


  order by T.${sortColumn} ${sortOrder}


  order by T.ID_

mapper.sql:

from ACT_TASK T

  inner join ACT_TASKINVOLVEMENT I on I.TASK_ID_ = T.ID_



    T.NAME_ = #{name}


    and T.ASSIGNEE_ = #{assignee}


    and 
      ( 
        T.PROC_INST_ID_ = #{processInstanceId}
        OR T.EXECUTION_ID_ = #{processInstanceId}
      )


    and T.ASSIGNEE_ is null
    and I.TYPE_ = 'candidate'
    and 
    ( 

        I.USER_ID_ = #{candidateUser}          


        or


        I.GROUP_ID_ IN

          #{group}


    )

mapper.update:
update ACT_TASK
NAME_ = #{name, jdbcType=VARCHAR}, PRIORITY_ = #{priority, jdbcType=INTEGER}, CREATE_TIME_ = #{createTime, jdbcType=TIMESTAMP}, SKIPPABLE_ = #{skippable, jdbcType=BOOLEAN}, START_DEADLINE_ = #{startDeadline, jdbcType=TIMESTAMP}, COMPLETION_DEADLINE_ = #{completionDeadline, jdbcType=TIMESTAMP}, ASSIGNEE_ = #{assignee, jdbcType=VARCHAR}, EXECUTION_ID_ = #{executionId, jdbcType=VARCHAR}, PROC_INST_ID_ = #{processInstanceId, jdbcType=VARCHAR}, PROC_DEF_ID_ = #{processDefinitionId, jdbcType=VARCHAR}
where ID_= #{id, jdbcType=VARCHAR}

update ACT_TASKINVOLVEMENT

  TYPE_ = #{type, jdbcType=VARCHAR},
  USER_ID_ = #{userId, jdbcType=VARCHAR},
  GROUP_ID_ = #{groupId, jdbcType=VARCHAR},
  TASK_ID_ = #{taskId, jdbcType=VARCHAR}

where ID_ = #{id, jdbcType=VARCHAR}
